mapper.insert:
INSERT INTO calendar
(
	seq
	, team_name
	, title
	, description
	, startdate
	, enddate
	
)
VALUES
(
	SEQ_calendar_seq.NEXTVAL
	, #{team_name}
	, #{title}
	, #{description}
	, #{startdate}
	, #{enddate}
)

insert.foreach:
 
			into calendar
			(
				seq
				,team_name
				,title
				,description
				,startdate
				,enddate
			)
			values
			(
				get_seq_calendar_no()
				,#{item.team_name}
				,'MATCH'
				,#{item.match_no}
				,#{item.matchdate}
				,#{item.matchdate}
			)
			into calendar
			(
				seq
				,team_name
				,title
				,description
				,startdate
				,enddate
			)
			values
			(
				get_seq_calendar_no()
				,#{item.away_team_name}
				,'MATCH'
				,#{item.match_no}
				,#{item.matchdate}
				,#{item.matchdate}
			)
