mapper.update:
update
	player
set

	
		
		team_name = #{team_name}
		
		
		team_name = null
		
	
	
		authority = #{authority}
	
	
		email =#{email}
		,name =#{name}
		,position =#{position}
		,subposition =#{subposition}
		,height =#{height}
		,weight =#{weight}
		,birth =#{birth}
		,mainfoot =#{mainfoot}
		,player_comment =#{player_comment}
	

where
	player_id = #{player_id}

mapper.select:
Select player_id
From player
Where name = #{name} AND email = #{email}

Select password
From player
Where player_id = #{player_id} AND email = #{email}
